/*!
 * Get bitmap resources
 */

wxBitmap InfoDialog::GetBitmapResource( const wxString& name )
{
    // Bitmap retrieval
////@begin InfoDialog bitmap retrieval
    wxUnusedVar(name);
    if (name == _T("caen.png"))
    {
        wxBitmap bitmap(AppSettings::get_default_path()+ _T("caen.png"), wxBITMAP_TYPE_PNG);
        return bitmap;
    }
    return wxNullBitmap;
////@end InfoDialog bitmap retrieval
}

